// Frontend function that makes the HTTP call\nfunction getSearchResults($query) {\n    // Instead of making an actual HTTP request, call the backend function directly\n    ob_start();\n    $results = backendSearch($query);\n    $output = ob_get_clean();\n    \n    // Return the results as they would be returned from the HTTP call\n    return $output;\n}\n\n// Backend function that processes the request\nfunction backendSearch($query) {\n    // Simulate processing the search query\n    $results = [\n        'result1' => 'Search Result 1 for ' . $query,\n        'result2' => 'Search Result 2 for ' . $query,\n    ];\n    \n    // Simulate output as if it was an HTTP response\n    echo json_encode($results);\n}\n\n// Example usage\n$query = 'example search';\n$response = getSearchResults($query);\necho "Search results: " . $response;\n\n// Now you can debug through backendSearch function and see the results