$xmlString = <<<XML\n<object>\n  <data type="me">myname</data>\n  <data type="you">yourname</data>\n  <data type="me">myothername</data>\n</object>\nXML;\n\n$xml = simplexml_load_string($xmlString);\n\n// Use XPath to select all <data> elements with type="me"\n$result = $xml->xpath('//data[@type="me"]');\n\n// Print the contents of the selected elements\nforeach ($result as $data) {\n    echo $data . "\n";\n}