$array = [\n    'item1' => [\n        'dataset' => [],\n        3 => [],\n        7 => [],\n    ],\n    'item2' => [\n        'dataset' => [],\n        4 => [],\n        19 => [],\n        2 => [],\n    ],\n];\n\nforeach ($array as &$item) {\n    // Remove 'dataset' and store it\n    $dataset = $item['dataset'];\n    unset($item['dataset']);\n    \n    // Add 'dataset' back to the end\n    $item['dataset'] = $dataset;\n}\n\n// Now $array has 'dataset' as the last index in each sub-array\nprint_r($array);