<?php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$q = "SELECT * FROM users WHERE username = 'doesnotexist'";\n$r = $mysqli->query($q);\n\nif ($r === FALSE) {\n    echo "<p>Query Error!</p>";\n} else {\n    if ($r->num_rows == 0) {\n        echo "<p>NOT FOUND!</p>";\n    } else {\n        echo "<p>FOUND!</p>";\n    }\n}\n\n$mysqli->close();\n?>