<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to fetch random entries\n$sql = "SELECT QNO FROM TABLE ORDER BY RAND() LIMIT 10";\n$result = $mysqli->query($sql);\n\n// Initialize an array to hold the entries\n$qnoArray = array();\n\n// Fetch the results and save them into the array\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        $qnoArray[] = $row['QNO'];\n    }\n} else {\n    echo "0 results";\n}\n\n// Close the connection\n$mysqli->close();\n\n// Print the array (for debugging purposes)\nprint_r($qnoArray);\n?>