<?php\n// Database connection\n$host = 'your_host';\n$db = 'your_database';\n$user = 'your_username';\n$pass = 'your_password';\n\n$dsn = "mysql:host=$host;dbname=$db;charset=utf8mb4";\n$options = [\n    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES => false,\n];\n\ntry {\n    $pdo = new PDO($dsn, $user, $pass, $options);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// The location we want to search for\n$location = 'France';\n\n// Prepare and execute the query\n$sql = "\nSELECT e.*\nFROM entry e\nJOIN entry_locations el ON e.id = el.entry_id\nJOIN location l ON el.location_id = l.id\nWHERE l.name = :location\n";\n\n$stmt = $pdo->prepare($sql);\n$stmt->execute(['location' => $location]);\n\n// Fetch the results\n$entries = $stmt->fetchAll();\n\n// Display the results\nforeach ($entries as $entry) {\n    echo 'Name: ' . htmlspecialchars($entry['name']) . '<br>';\n    echo 'Address: ' . htmlspecialchars($entry['address']) . '<br>';\n    echo 'Image: ' . htmlspecialchars($entry['image']) . '<br>';\n    echo '<hr>';\n}\n?>