function renderPage($pageName, $urlParts) {\n         $pageConfig = getPageConfig($pageName);\n\n         // Load each module in its position\n         foreach ($pageConfig as $module) {\n             // Render module\n             $moduleName = ucfirst($module['module_name']) . 'Module';\n             $moduleInstance = new $moduleName();\n             echo "<div class='{$module['position']}'>";\n             $moduleInstance->render();\n             echo "</div>";\n         }\n\n         // Route the requested module\n         routeModule($urlParts, $pageConfig);\n     }\n\n     // Example usage\n     $urlParts = explode('/', trim($_SERVER['REQUEST_URI'], '/'));\n     $pageName = $urlParts[0];\n     renderPage($pageName, array_slice($urlParts, 1));