function routeModule($urlParts, $pageConfig) {\n         foreach ($pageConfig as $module) {\n             if ($module['module_name'] == $urlParts[0]) {\n                 // Load and call the module's method with remaining URL parts\n                 $moduleName = ucfirst($module['module_name']) . 'Module';\n                 $methodName = $urlParts[1];\n                 $params = array_slice($urlParts, 2);\n                 \n                 if (class_exists($moduleName) && method_exists($moduleName, $methodName)) {\n                     $moduleInstance = new $moduleName();\n                     call_user_func_array([$moduleInstance, $methodName], $params);\n                 }\n                 break;\n             }\n         }\n     }