<?php\nfunction getRemoteFileInfo($url) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_NOBODY, true);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_FILETIME, true);\n    curl_setopt($ch, CURLOPT_HEADER, true);\n    \n    $headers = curl_exec($ch);\n\n    if ($headers === false) {\n        return false;\n    }\n\n    $fileInfo = [\n        'last_modified' => curl_getinfo($ch, CURLINFO_FILETIME),\n        'content_type' => curl_getinfo($ch, CURLINFO_CONTENT_TYPE),\n        'content_length' => curl_getinfo($ch, CURLINFO_CONTENT_LENGTH_DOWNLOAD),\n    ];\n\n    curl_close($ch);\n    return $fileInfo;\n}\n\n$url = "http://source.com/file.mp3";\n$fileInfo = getRemoteFileInfo($url);\n\nif ($fileInfo !== false && $fileInfo['last_modified'] != -1) {\n    echo "Last Modified: " . date("Y-m-d H:i:s", $fileInfo['last_modified']) . "\n";\n    echo "Content Type: " . $fileInfo['content_type'] . "\n";\n    echo "Content Length: " . $fileInfo['content_length'] . " bytes\n";\n} else {\n    echo "Unable to retrieve file information.\n";\n}\n?>