<?php\nsession_start();\n\n// Check if user is authenticated\nif (!isset($_SESSION['user']) || $_SESSION['role'] !== 'admin') {\n    header('Location: login.php');\n    exit();\n}\n\n// Whitelist of allowed files\n$allowed_files = [\n    'dashboard' => 'admin/dashboard.php',\n    'settings' => 'admin/settings.php',\n    'users' => 'admin/users.php',\n];\n\n// Get the requested page from the query string\n$page = isset($_GET['page']) ? $_GET['page'] : 'dashboard';\n\n// Check if the requested page is in the whitelist\nif (array_key_exists($page, $allowed_files)) {\n    include $allowed_files[$page];\n} else {\n    // Default to dashboard if the page is not allowed\n    include $allowed_files['dashboard'];\n}\n?>