<?php\n\n// Sample multidimensional array\n$array = [\n  'docs' => [\n    '_c' => [\n      'folder' => [\n        '_c' => [\n          'name' => ['_v' => 'Folder name'],\n          'date' => ['_v' => '20.06.2009'],\n          'folder' => [\n            [\n              '_c' => [\n                'name' => ['_v' => 'Subfolder1'],\n                'date' => ['_v' => '']\n              ]\n            ],\n            [\n              '_c' => [\n                'name' => ['_v' => 'Subfolder2'],\n                'date' => ['_v' => '']\n              ]\n            ]\n          ]\n        ]\n      ],\n      'file' => [\n        '_c' => [\n          'name' => ['_v' => '']\n        ]\n      ]\n    ]\n  ]\n];\n\n// Path and new file to be added\n$path = ['docs', '_c', 'folder', '_c', 'folder', 1];\n$newfile = ['_c' => ['name' => ['_v' => 'NewFile'], 'date' => ['_v' => '']]];\n\n// Function to insert new file at specified path\nfunction addFileToPath(&$array, $path, $newfile) {\n    $ptr = &$array;\n    foreach ($path as $key) {\n        if (!isset($ptr[$key])) {\n            $ptr[$key] = [];\n        }\n        $ptr = &$ptr[$key];\n    }\n    if (!isset($ptr['_c'])) {\n        $ptr['_c'] = [];\n    }\n    $ptr['_c'][] = $newfile;\n}\n\n// Add the new file to the array\naddFileToPath($array, $path, $newfile);\n\n// Print the modified array\nprint_r($array);\n?>