<?php\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$jsonData = file_get_contents('products.json');\n$products = json_decode($jsonData, true);\n\nforeach ($products as $product) {\n    $sku = $mysqli->real_escape_string($product['sku']);\n    $name = $mysqli->real_escape_string($product['name']);\n    $description = $mysqli->real_escape_string($product['description']);\n    $price = $mysqli->real_escape_string($product['price']);\n\n    $sql = "INSERT INTO products (sku, name, description, price) VALUES ('$sku', '$name', '$description', '$price')\n            ON DUPLICATE KEY UPDATE name='$name', description='$description', price='$price'";\n\n    if (!$mysqli->query($sql)) {\n        echo "Error: " . $sql . "<br>" . $mysqli->error;\n    }\n}\n\n$mysqli->close();\n?>