// app/core/Controller.php - Base Controller Class\nclass Controller {\n    public function model($model) {\n        require_once '../app/models/' . $model . '.php';\n        return new $model();\n    }\n\n    public function view($view, $data = []) {\n        require_once '../app/views/' . $view . '.php';\n    }\n}