<?php\n$letters = range('A', 'Z');\necho '<em>Jump to:</em><ul class="letters">';\n// anchors\nforeach ($letters as $letter) {\n    echo '<li><a href="#botany-words-' . $letter . '">' . $letter . '</a></li>';\n}\necho '</ul>';\n\n// start listing them!\n$currentLetterIndex = 0;\n$currentLetter = $letters[$currentLetterIndex];\necho '<dl id="botany-words">';\n\nforeach ($content as $botanyWord => $definition) {\n    $firstLetter = strtoupper(substr($botanyWord, 0, 1));\n    \n    if ($currentLetterIndex == 0 || $firstLetter != $currentLetter) {\n        // Update current letter if different\n        $currentLetter = $firstLetter;\n        echo '<h3 id="botany-words-' . $currentLetter . '">' . $currentLetter . '</h3>';\n        \n        // Update current letter index\n        if (($currentLetterIndex = array_search($currentLetter, $letters)) === false) {\n            $currentLetterIndex = 0;\n        }\n    }\n    ?>\n\n    <dt><?php echo $botanyWord; ?></dt>\n    <dd><?php echo $definition; ?></dd>\n\n    <?php\n}\necho '</dl>';\n?>