<?php\n// Database connection\n$dsn = 'mysql:host=your_host;dbname=your_db;charset=utf8mb4';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    die('Connection failed: ' . $e->getMessage());\n}\n\n// User input from a textbox\n$userInput = $_POST['textbox'];\n\n// Prepare and execute the SQL statement\n$sql = "INSERT INTO your_table (text_field) VALUES (:userInput)";\n$stmt = $pdo->prepare($sql);\n$stmt->bindParam(':userInput', $userInput, PDO::PARAM_STR);\n$stmt->execute();\n?>