<?php\n$cache_file = 'weather_cache.json';\n\nif (file_exists($cache_file)) {\n    $weather_data = json_decode(file_get_contents($cache_file), true);\n    if ($weather_data) {\n        echo 'Temperature: ' . $weather_data['temp'];\n        echo 'Wind: ' . $weather_data['wind'];\n        echo 'Wind Direction: ' . $weather_data['wind_dir'];\n        echo 'Gust: ' . $weather_data['gust'];\n        echo 'Observation Time: ' . $weather_data['time'];\n        echo 'Pressure: ' . $weather_data['pres'];\n        echo 'Weather: ' . $weather_data['weath'];\n    } else {\n        echo 'Unable to load weather data';\n    }\n} else {\n    echo 'Cache file not found';\n}\n?>