// Example function to execute SVN commands securely\nfunction runSvnCommand($command, $args = [])\n{\n    // Sanitize command and arguments\n    $escapedCommand = escapeshellcmd($command);\n    $escapedArgs = array_map('escapeshellarg', $args);\n\n    // Construct the full command\n    $fullCommand = $escapedCommand . ' ' . implode(' ', $escapedArgs);\n\n    // Execute the command\n    $output = [];\n    $returnVar = 0;\n    exec($fullCommand, $output, $returnVar);\n\n    // Handle command output and return value\n    if ($returnVar !== 0) {\n        throw new RuntimeException("SVN command failed: " . implode("\n", $output));\n    }\n\n    return $output;\n}\n\n// Example usage\ntry {\n    $output = runSvnCommand('svn update', ['/path/to/repository']);\n    print_r($output);\n} catch (RuntimeException $e) {\n    echo "Error: " . $e->getMessage();\n}