function getTimeAgo($timestamp) {\n    $currentDate = new DateTime();\n    $postDate = new DateTime();\n    $postDate->setTimestamp($timestamp);\n\n    $interval = $currentDate->diff($postDate);\n    $daysAgo = $interval->days;\n\n    if ($daysAgo == 0) {\n        return $postDate->format('H:i');\n    } elseif ($daysAgo == 1) {\n        return 'yesterday';\n    } else {\n        return "$daysAgo days ago";\n    }\n}\n\n// Example usage\n$timestamp = strtotime('2023-10-05 14:30:00'); // Replace with your UNIX timestamp\necho getTimeAgo($timestamp);