<?php\n// Function to deliver a document inline to the browser\nfunction deliverDocument($filePath, $fileName, $contentType) {\n    // Check if file exists\n    if (!file_exists($filePath)) {\n        header("HTTP/1.0 404 Not Found");\n        exit;\n    }\n\n    // Get the file size\n    $fileSize = filesize($filePath);\n\n    // Set headers\n    header("Content-Type: $contentType");\n    header("Content-Disposition: inline; filename=\"$fileName\"");\n    header("Accept-Ranges: bytes");\n    header("Content-Length: $fileSize");\n    header("Expires: 0");\n    header("Cache-Control: must-revalidate, post-check=0, pre-check=0");\n    header("Pragma: public");\n\n    // Read the file and output its content\n    readfile($filePath);\n    exit;\n}\n\n// Example usage\n$filePath = 'path/to/your/document.pdf';\n$fileName = 'document.pdf';\n$contentType = 'application/pdf'; // Modify content type based on the document type\n\ndeliverDocument($filePath, $fileName, $contentType);\n?>