<?php\n// List of processing servers\n$servers = [\n    'http://server1.example.com',\n    'http://server2.example.com',\n    'http://server3.example.com',\n];\n\n// Path to a file to store the last used server index\n$lastServerFile = 'last_server.txt';\n\n// Get the last used server index\nif (file_exists($lastServerFile)) {\n    $lastServerIndex = (int) file_get_contents($lastServerFile);\n} else {\n    $lastServerIndex = -1;\n}\n\n// Determine the next server index\n$nextServerIndex = ($lastServerIndex + 1) % count($servers);\n\n// Store the next server index for future use\nfile_put_contents($lastServerFile, $nextServerIndex);\n\n// Get the next server URL\n$nextServer = $servers[$nextServerIndex];\n\n// Process the job on the selected server\n// Example of making a request to the selected server\n$jobData = ['param1' => 'value1', 'param2' => 'value2'];\n$options = [\n    'http' => [\n        'header'  => "Content-type: application/x-www-form-urlencoded\r\n",\n        'method'  => 'POST',\n        'content' => http_build_query($jobData),\n    ],\n];\n$context  = stream_context_create($options);\n$result = file_get_contents($nextServer . '/process_job.php', false, $context);\n\n// Check the result of the job processing\nif ($result === FALSE) {\n    die('Error processing job');\n}\n\n// Output the result\necho $result;\n?>