<?php\n$created_time = 'Thu, 18 Jun 2009 21:44:49 +0000';\n$utc_offset = -28800;\n\n// Create a DateTime object from the created time\n$date = new DateTime($created_time, new DateTimeZone('UTC'));\n\n// Convert the UTC offset from seconds to hours\n$offset_hours = $utc_offset / 3600;\n\n// Create a DateTimeZone object for the local time zone\n$local_timezone = timezone_name_from_abbr("", $utc_offset, true);\nif ($local_timezone === false) {\n    $local_timezone = timezone_name_from_abbr("", $utc_offset, false);\n}\n\n// Change the timezone of the DateTime object to the local time zone\n$date->setTimezone(new DateTimeZone($local_timezone));\n\n// Format the date in a "pretty" format\necho $date->format('l jS \of F Y h:i:s A');\n?>