<?php\nclass Car {\n    // Properties\n    public $color;\n    public $model;\n\n    // Constructor\n    public function __construct($color, $model) {\n        $this->color = $color;\n        $this->model = $model;\n    }\n\n    // Method\n    public function message() {\n        return "My car is a " . $this->color . " " . $this->model . ".";\n    }\n}\n\n// Create an object\n$myCar = new Car("black", "Volvo");\necho $myCar->message();\n?>