<?php\n// Example dates from the database\n$date1 = '11/11/2002'; // Format d/m/Y\n$date2 = '11-11-2002'; // Format d-m-Y\n\n// Desired output format\n$dateFormat = 'm/d/Y';\n\n// Convert and format Date 1\n$dateTime1 = DateTime::createFromFormat('d/m/Y', $date1);\n$formattedDate1 = $dateTime1->format($dateFormat);\n\n// Convert and format Date 2\n$dateTime2 = DateTime::createFromFormat('d-m-Y', $date2);\n$formattedDate2 = $dateTime2->format($dateFormat);\n\n// Display the formatted dates\necho "Formatted Date 1: " . $formattedDate1 . "\n";\necho "Formatted Date 2: " . $formattedDate2 . "\n";\n?>