$encryptedBase64 = "ENCRYPTED_STRING_FROM_JAVA";\n$key = "1234567890123456"; // The same 16-byte key\n$iv = "abcdefghijklmnop"; // The same 16-byte IV\n\n// Convert key and IV to binary format\n$key = hex2bin(bin2hex($key));\n$iv = hex2bin(bin2hex($iv));\n\n// Decode base64 encoded string\n$encrypted = base64_decode($encryptedBase64);\n\n// Decrypt\n$decrypted = openssl_decrypt($encrypted, 'AES-128-CBC', $key, OPENSSL_RAW_DATA, $iv);\n\necho "Decrypted: " . $decrypted;