// Email processing script (e.g., process_email_queue.php)\n   require 'path/to/your/bootstrap.php'; // Adjust the path to your Zend Framework bootstrap file\n\n   $db = Zend_Db_Table::getDefaultAdapter();\n   $select = $db->select()->from('email_queue')->where('status = ?', 'pending');\n   $rows = $db->fetchAll($select);\n\n   foreach ($rows as $row) {\n       try {\n           $mail = new Zend_Mail();\n           $mail->setFrom('your-email@example.com', 'Your Name');\n           $mail->addTo($row['recipient_email']);\n           $mail->setSubject($row['subject']);\n           $mail->setBodyText($row['body']);\n           $mail->send();\n\n           // Update status to 'sent'\n           $db->update('email_queue', ['status' => 'sent'], 'id = ' . $row['id']);\n       } catch (Exception $e) {\n           // Update status to 'failed'\n           $db->update('email_queue', ['status' => 'failed'], 'id = ' . $row['id']);\n       }\n   }