class Custom_Walker_Category extends Walker_Category {\n    // Start the element output.\n    function start_el( &$output, $category, $depth = 0, $args = array(), $id = 0 ) {\n        $cat_name = esc_attr( $category->name );\n        $cat_name = apply_filters( 'list_cats', $cat_name, $category );\n        $link = '<a href="' . esc_url( get_term_link( $category ) ) . '" ';\n        $link .= 'title="' . esc_attr( $category->description ) . '"';\n        $link .= ' id="category-' . $category->term_id . '">';\n        $link .= $cat_name . '</a>';\n\n        $output .= '<li id="cat-item-' . $category->term_id . '" class="cat-item cat-item-' . $category->term_id;\n        if ( isset( $args['current_category'] ) && $category->term_id == $args['current_category'] ) {\n            $output .= ' current-cat';\n        }\n        $output .= '">' . $link . "\n";\n    }\n}