<?php\n$request = 'http://www.ratecatcher.com/prototype.htm'; // Replace with your actual request URL\n$xmlContent = file_get_contents($request);\n\n// Load the XML content into a DOMDocument\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress XML parsing errors for better handling\n$dom->loadXML($xmlContent);\nlibxml_clear_errors();\n\n// Create a new XSLTProcessor\n$xsl = new DOMDocument;\n$xsl->load('your-stylesheet.xsl'); // Load your XSLT stylesheet if you have one\n\n$proc = new XSLTProcessor;\n$proc->importStyleSheet($xsl);\n\n// Transform the XML into HTML\n$html = $proc->transformToXML($dom);\n\n// Output the HTML content\necho $html;\n?>