function shortHash($input) {\n    // Generate a longer hash using a secure hash algorithm (SHA-256)\n    $hash = hash('sha256', $input);\n    \n    // Convert the first 8 characters of the hash to a base62 string\n    $base62 = base_convert(substr($hash, 0, 16), 16, 62);\n    \n    // Return the first 8 characters of the base62 string\n    return substr($base62, 0, 8);\n}\n\n// Example usage\n$inputString = "example string or file content";\n$shortHash = shortHash($inputString);\necho $shortHash;