<?php\nfunction getMonthName($offset) {\n    $date = new DateTime();\n    $date->modify("first day of +{$offset} month");\n    return $date->format('F');\n}\n\n$months = [];\nfor ($i = 0; $i < 12; $i++) {\n    $months[] = getMonthName($i);\n}\n\nforeach ($months as $month) {\n    echo '<a href="http://mydomain.com/'.strtolower($month).'/" title="'.ucfirst($month).'">'.$month.'</a><br />';\n}\n?>