// To hash a password\nfunction createPasswordHash($password) {\n    $options = [\n        'cost' => 12, // The cost parameter can be adjusted based on your server capabilities\n    ];\n    return password_hash($password, PASSWORD_BCRYPT, $options);\n}\n\n// To verify a password\nfunction verifyPassword($password, $hash) {\n    return password_verify($password, $hash);\n}\n\n// Example usage\n$username = 'user';\n$password = 'securePassword123';\n\n// Create a hash of the password\n$hashedPassword = createPasswordHash($password);\n\n// Store $hashedPassword in your database associated with the username\n\n// Later, when verifying the login\n$isPasswordCorrect = verifyPassword($password, $hashedPassword);\nif ($isPasswordCorrect) {\n    echo "Password is correct!";\n} else {\n    echo "Invalid password.";\n}