function my_custom_function($args = array()) {\n    // Define default arguments\n    $defaults = array(\n        'title' => 'Default Title',\n        'show' => true,\n        'count' => 10,\n    );\n\n    // Merge user-provided arguments with defaults\n    $args = array_merge($defaults, $args);\n\n    // Access the arguments\n    $title = $args['title'];\n    $show = $args['show'];\n    $count = $args['count'];\n\n    // Your function logic here\n    if ($show) {\n        echo "<h1>$title</h1>";\n        echo "<p>Count: $count</p>";\n    }\n}\n\n// Usage\nmy_custom_function(array(\n    'title' => 'Custom Title',\n    'count' => 5\n));