function getArrayDepth($array) {\n    if (!is_array($array)) {\n        return 0;\n    }\n    $maxDepth = 0;\n    foreach ($array as $item) {\n        $maxDepth = max($maxDepth, getArrayDepth($item));\n    }\n    return $maxDepth + 1;\n}\n\n// Example usage\n$array2D = [[1, 2], [3, 4]];\n$array3D = [[[1, 2]], [[3, 4]]];\n\n$depth2D = getArrayDepth($array2D); // Should return 2\n$depth3D = getArrayDepth($array3D); // Should return 3