function normalizeArrayDepth($array) {\n    // Normalize to 3 levels deep\n    if (!isset($array[0])) {\n        $array = [$array];\n    }\n    if (!isset($array[0][0])) {\n        $array[0] = [$array[0]];\n    }\n    if (!isset($array[0][0][0])) {\n        $array[0][0] = [$array[0][0]];\n    }\n    return $array;\n}\n\n// Example usage\n$array2D = [[1, 2], [3, 4]];\n$array3D = [[[1, 2]], [[3, 4]]];\n\n$normalizedArray2D = normalizeArrayDepth($array2D);\n$normalizedArray3D = normalizeArrayDepth($array3D);\n\n// Both $normalizedArray2D and $normalizedArray3D will now be 3 levels deep