<?php\n\nif (!isset($_GET['upload'])) { ?>\n  <form method="post" action="index.php?upload=true" enctype="multipart/form-data">\n    <input type="file" name="file" class="form">\n    <input name="submit" type="submit">\n  </form>\n<?php } else if (isset($_GET['upload']) && $_GET['upload'] == 'true') {\n  if (isset($_FILES['file']) && $_FILES['file']['error'] == UPLOAD_ERR_OK) {\n    $uploadDir = '/path/to/your/upload/directory/';\n    $uploadFile = $uploadDir . basename($_FILES['file']['name']);\n    \n    // Check if the directory is writable\n    if (is_writable($uploadDir)) {\n      if (move_uploaded_file($_FILES['file']['tmp_name'], $uploadFile)) {\n        echo "Success!";\n      } else {\n        echo "Error: Could not move uploaded file.";\n      }\n    } else {\n      echo "Error: Upload directory is not writable.";\n    }\n  } else {\n    echo "Error: File upload failed. " . $_FILES['file']['error'];\n  }\n}\n?>