function paginateContent($content, $maxChars) {\n    $content = trim($content);\n    $length = mb_strlen($content);\n    $pages = [];\n    $start = 0;\n\n    while ($start < $length) {\n        $pageContent = mb_substr($content, $start, $maxChars);\n        \n        // Ensure we don't split words or HTML tags\n        if (mb_strlen($pageContent) == $maxChars) {\n            $lastSpace = mb_strrpos($pageContent, ' ');\n            $lastTag = mb_strrpos($pageContent, '>');\n            \n            // Determine the split point\n            if ($lastTag !== false && $lastTag > $lastSpace) {\n                $splitPoint = $lastTag + 1;\n            } else {\n                $splitPoint = $lastSpace;\n            }\n            \n            $pageContent = mb_substr($pageContent, 0, $splitPoint);\n        }\n\n        $pages[] = $pageContent;\n        $start += mb_strlen($pageContent);\n    }\n\n    return $pages;\n}\n\n// Example usage\n$content = "<p>This is a sample content with <b>HTML</b> tags. It needs to be paginated without breaking words or tags.</p>";\n$maxChars = 50; // Maximum characters per page\n\n$pages = paginateContent($content, $maxChars);\n\nforeach ($pages as $index => $page) {\n    echo "<h2>Page " . ($index + 1) . "</h2>";\n    echo $page;\n}