// In your ItemsController\npublic function edit($id, $page = 1)\n{\n    $item = $this->Items->get($id);\n    if ($this->request->is(['post', 'put'])) {\n        $this->Items->patchEntity($item, $this->request->getData());\n        if ($this->Items->save($item)) {\n            $this->Flash->success(__('Item has been updated.'));\n            return $this->redirect(['action' => 'index', 'page' => $page]);\n        }\n        $this->Flash->error(__('Unable to update item.'));\n    }\n    $this->set('item', $item);\n}