function sanitize_input($content) {\n    // Allow only certain HTML tags\n    $allowed_tags = '<b><i><ul><ol><li>';\n    $content = strip_tags($content, $allowed_tags);\n    \n    // Encode special characters to prevent XSS\n    $content = htmlspecialchars($content, ENT_QUOTES, 'UTF-8');\n    \n    // Optionally, you can also trim whitespace and validate length\n    $content = trim($content);\n    if (strlen($content) > 1000) { // assuming 1000 is the max length\n        $content = substr($content, 0, 1000);\n    }\n    \n    return $content;\n}\n\n// Usage example\n$user_input = $_POST['textarea_input'];\n$sanitized_input = sanitize_input($user_input);\necho $sanitized_input;