<?php\nrequire 'vendor/autoload.php';\n\nuse Parsedown;\nuse HTMLPurifier;\nuse HTMLPurifier_Config;\n\n// Initialize Parsedown\n$parsedown = new Parsedown();\n\n// Initialize HTML Purifier\n$config = HTMLPurifier_Config::createDefault();\n$config->set('HTML.AllowedElements', 'p,b,i,a,blockquote,code,ul,ol,li');\n$config->set('HTML.AllowedAttributes', 'a.href,a.title');\n$purifier = new HTMLPurifier($config);\n\n// Markdown text input\n$markdownText = "Your markdown input here";\n\n// Convert Markdown to HTML\n$html = $parsedown->text($markdownText);\n\n// Purify the HTML\n$safeHtml = $purifier->purify($html);\n\necho $safeHtml;\n?>