<?php\n\ndefine('DEFAULT_ENCRYPTION_KEY', 'asdHRMfjkahguglw84tlrogl9y8kamaFDaufasds');\ndefine('ENCRYPTION_METHOD', 'AES-256-CBC');\n\nclass Encryption\n{\n    private $key;\n    private $method;\n\n    function __construct($key = DEFAULT_ENCRYPTION_KEY, $method = ENCRYPTION_METHOD)\n    {\n        $this->key = hash('sha256', $key, true);\n        $this->method = $method;\n    }\n\n    public function encrypt($data)\n    {\n        $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length($this->method));\n        $encrypted = openssl_encrypt($data, $this->method, $this->key, 0, $iv);\n        return base64_encode($iv . $encrypted);\n    }\n\n    public function decrypt($data)\n    {\n        $data = base64_decode($data);\n        $iv_length = openssl_cipher_iv_length($this->method);\n        $iv = substr($data, 0, $iv_length);\n        $encrypted_data = substr($data, $iv_length);\n        return openssl_decrypt($encrypted_data, $this->method, $this->key, 0, $iv);\n    }\n}\n\n$crypt = new Encryption();\n$enc = $crypt->encrypt('hello world');\necho "Encrypted: " . $enc . "\n";\n\n$dec = $crypt->decrypt($enc);\necho "Decrypted: " . $dec . "\n";