function searchByKeyValue($array, $searchKey, $searchValue) {\n    $result = array();\n\n    foreach ($array as $key => $value) {\n        if (is_array($value)) {\n            $subResult = searchByKeyValue($value, $searchKey, $searchValue);\n            if (!empty($subResult)) {\n                $result = array_merge($result, $subResult);\n            }\n        } elseif ($key == $searchKey && $value == $searchValue) {\n            return array($array);\n        }\n    }\n\n    return $result;\n}\n\n// Example usage\n$arr = array(\n    0 => array('id' => 1, 'name' => "cat 1"),\n    1 => array('id' => 2, 'name' => "cat 2"),\n    2 => array('id' => 3, 'name' => "cat 1")\n);\n\n$searchKey = 'name';\n$searchValue = 'cat 1';\n$result = searchByKeyValue($arr, $searchKey, $searchValue);\n\nprint_r($result);