session_start();\n\n$session_id = session_id();\n$query = $dbh->prepare("SELECT a_session FROM sessions WHERE id = :session_id LIMIT 1");\n$query->execute([':session_id' => $session_id]);\n$session_data = $query->fetchColumn();\n\nif ($session_data) {\n    // Decode the Perl session data\n    // This example assumes the session data is serialized in a simple format\n    // You may need to adjust this based on your actual data format\n    $session_data = unserialize($session_data);\n    print_r($session_data);\n} else {\n    echo "No session data found.";\n}