for($i = 0; $i < $numRecords2; $i++) {\n    $prod_id = $_SESSION['prod_array'][$frame_id[$i]];\n    echo "\n    <script>\n        $('#frame$frame_id[$i]').data('index', $i);\n        $('#frame$frame_id[$i]').click(function() {\n            var index = $(this).data('index');\n            $('#top-left-frame').html('<img src=\"' + upper_left[index] + '\" alt=\"\" />');\n            $('#top-mid-frame').css('background-image', 'url(' + upper_middle[index] + ')');\n            $('#top-right-frame').html('<img src=\"' + upper_right[index] + '\" alt=\"\" />');\n            $('#mid-left-frame').css('background-image', 'url(' + middle_left[index] + ')');\n            $('#mid-right-frame').css('background-image', 'url(' + middle_right[index] + ')');\n            $('#bottom-left-frame').html('<img src=\"' + bottom_left[index] + '\" alt=\"\" />');\n            $('#bottom-mid-frame').css('background-image', 'url(' + bottom_middle[index] + ')');\n            $('#bottom-right-frame').html('<img src=\"' + bottom_right[index] + '\" alt=\"\" />');\n            $('#frame-select').html(prod_array[frame_id[index]]);\n            $('#pid-form').val(frame_id[index]);\n            $('#oa_id-form').val(prod_array[frame_id[index]]);\n            var frameState = $(this).attr('id');\n            $.cookie('frameState', frameState, { expires: 7 });\n            alert($.cookie('frameState') + ' was clicked.');\n        });\n    </script>\n    ";\n}