class Organization {\n    private $orgId;\n    private $orgName;\n\n    public function __construct($orgId, $orgName) {\n        $this->orgId = $orgId;\n        $this->orgName = $orgName;\n    }\n\n    public function getOrgId() {\n        return $this->orgId;\n    }\n\n    public function getOrgName() {\n        return $this->orgName;\n    }\n\n    public function setOrgName($orgName) {\n        $this->orgName = $orgName;\n    }\n\n    // Example of adding behavior to the domain model\n    public function changeName($newName) {\n        if (empty($newName)) {\n            throw new InvalidArgumentException("Organization name cannot be empty");\n        }\n        $this->orgName = $newName;\n    }\n}