function isValidRFC2616Date($dateString) {\n    $rfc2616Regex = '/^(?:Mon|Tue|Wed|Thu|Fri|Sat|Sun), (\d{2}) (?:Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec) (\d{4}) (\d{2}):(\d{2}):(\d{2}) GMT$/';\n\n    if (preg_match($rfc2616Regex, $dateString, $matches)) {\n        $dateTime = DateTime::createFromFormat('D, d M Y H:i:s e', $dateString);\n        if ($dateTime === false) {\n            return false;\n        }\n\n        $currentTime = new DateTime('now', new DateTimeZone('GMT'));\n        $interval = $currentTime->getTimestamp() - $dateTime->getTimestamp();\n\n        // Check if the date is within 15 minutes (900 seconds)\n        return abs($interval) <= 900;\n    }\n\n    return false;\n}\n\nif (isset($_SERVER['HTTP_DATE'])) {\n    $dateHeader = $_SERVER['HTTP_DATE'];\n    if (isValidRFC2616Date($dateHeader)) {\n        echo "Date header is valid and within the allowed time frame.";\n        // Proceed with your logic\n    } else {\n        echo "Invalid or out-of-range Date header.";\n        // Handle the error\n    }\n} else {\n    echo "Date header is missing.";\n    // Handle the error\n}