<?php\n// Database connection details\n$host = 'localhost';\n$dbname = 'your_database_name';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create a new PDO instance\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Define the table name\n    $tableName = 'your_table_name';\n\n    // Prepare and execute the query to get column information\n    $stmt = $pdo->prepare("\n        SELECT COLUMN_NAME, CHARACTER_MAXIMUM_LENGTH\n        FROM information_schema.COLUMNS\n        WHERE TABLE_SCHEMA = :database AND TABLE_NAME = :table AND DATA_TYPE = 'varchar'\n    ");\n    $stmt->execute([\n        ':database' => $dbname,\n        ':table' => $tableName\n    ]);\n\n    // Fetch and display the column lengths\n    $columns = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    foreach ($columns as $column) {\n        echo "Column: " . $column['COLUMN_NAME'] . " - Max Length: " . $column['CHARACTER_MAXIMUM_LENGTH'] . "\n";\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>