<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get tables\n$tables = $conn->query("SHOW TABLES");\n$dot = "digraph G {\n";\n\nwhile ($table = $tables->fetch_array()) {\n    $tablename = $table[0];\n    $dot .= "  \"$tablename\" [shape=box];\n";\n    \n    // Get columns and foreign keys\n    $columns = $conn->query("SHOW COLUMNS FROM $tablename");\n    while ($column = $columns->fetch_assoc()) {\n        if (strpos($column['Field'], '_id') !== false) {\n            $foreign_table = substr($column['Field'], 0, -3);\n            $dot .= "  \"$tablename\" -> \"$foreign_table\";\n";\n        }\n    }\n}\n\n$dot .= "}\n";\n$conn->close();\n\n// Save to .dot file\nfile_put_contents('schema.dot', $dot);\n\necho "Schema dot file created.\n";\n?>