<?php\n\n$query = mysql_query("SELECT threads.id as thread_id, users.id as user_id, threads.*, users.*\n                      FROM threads\n                      INNER JOIN users\n                      ON threads.poster = users.id\n                      WHERE threads.forum_id = 11");\n\nwhile($row = mysql_fetch_array($query)) {\n    echo '<a href="thread.php?id=' . $row['thread_id'] . '">Link</a>';\n}\n?>