$conn = mysqli_connect("your_host", "your_username", "your_password", "your_database");\n\n$sql = "SELECT SUM(total_price) AS total_price \n        FROM (\n            SELECT SUM(price) AS total_price FROM `table_A`\n            UNION ALL\n            SELECT SUM(price) AS total_price FROM `table_B`\n        ) AS combined_totals";\n\n$res = mysqli_query($conn, $sql);\n\nif (mysqli_num_rows($res) > 0) {\n    $row = mysqli_fetch_assoc($res);\n    echo '<h3>' . number_format($row["total_price"], 0) . '</h3>';\n} else {\n    echo '<h3>0</h3>';\n}\n\necho mysqli_error($conn);\n\nmysqli_close($conn);