// a beautiful multidimensional array\npublic $form = array(\n    array(\n        'field' => 'email',\n        'params' => array(\n            array(\n                'rule' => 'email',\n                'on' => 'create',\n                'required' => true,\n            ),\n            array(\n                'rule' => 'email',\n                'on' => 'update',\n                'required' => false,\n            )\n        )\n    )\n);\n\n// beautiful foreach loops\npublic function validate($form) {\n    foreach ($form as $valueA) {\n        $field = $valueA['field'];\n\n        foreach ($valueA['params'] as $valueB) {\n            $rule = $valueB['rule'];\n            $on = $valueB['on'];\n            $required = $valueB['required'];\n\n            $this->isValid($field, $rule, $on, $required);\n        }\n    }\n}\n\n// Dummy isValid function for demonstration\npublic function isValid($field, $rule, $on, $required) {\n    // Validation logic here\n    echo "Validating field: $field, rule: $rule, on: $on, required: $required\n";\n}