<?php\n$search_string = $_POST['search_string']; // Get the search string from the form input.\n$search_string = '%' . $search_string . '%'; // Prepare the search string for the LIKE clause.\n\n// Database connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare the SQL statement with placeholders\n$sql = "SELECT \n            u.fname, \n            u.lname, \n            u.role, \n            u.username, \n            r.building AS ra_building, \n            r.room AS ra_room, \n            r.region AS ra_region, \n            a.office AS ac_office, \n            a.office_num AS ac_office_num\n        FROM \n            users u \n        LEFT JOIN \n            ra_ca r ON u.username = r.username \n        LEFT JOIN \n            area_coord a ON u.username = a.username\n        WHERE \n            u.fname LIKE ? OR \n            u.lname LIKE ? OR \n            r.building LIKE ? OR \n            r.region LIKE ? OR \n            a.office_num LIKE ?";\n\n// Prepare and bind\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("sssss", $search_string, $search_string, $search_string, $search_string, $search_string);\n\n// Execute the statement\n$stmt->execute();\n\n// Get the result\n$result = $stmt->get_result();\n\n// Fetch data\nwhile ($row = $result->fetch_assoc()) {\n    echo "First Name: " . $row['fname'] . " Last Name: " . $row['lname'] . "<br>";\n    // Output other fields as needed\n}\n\n// Close the statement and connection\n$stmt->close();\n$conn->close();\n?>